require 'spec_helper'

# This spec was generated by rspec-rails when you ran the scaffold generator.
# It demonstrates how one might use RSpec to specify the controller code that
# was generated by the Rails when you ran the scaffold generator.

describe ArtefactsController do

  def mock_artefact(stubs={})
    @mock_artefact ||= mock_model(Artefact, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all artefacts as @artefacts" do
      Artefact.stub(:all) { [mock_artefact] }
      get :index
      assigns(:artefacts).should eq([mock_artefact])
    end
  end

  describe "GET show" do
    it "assigns the requested artefact as @artefact" do
      Artefact.stub(:find).with("37") { mock_artefact }
      get :show, :id => "37"
      assigns(:artefact).should be(mock_artefact)
    end
  end

  describe "GET new" do
    it "assigns a new artefact as @artefact" do
      Artefact.stub(:new) { mock_artefact }
      get :new
      assigns(:artefact).should be(mock_artefact)
    end
  end

  describe "GET edit" do
    it "assigns the requested artefact as @artefact" do
      Artefact.stub(:find).with("37") { mock_artefact }
      get :edit, :id => "37"
      assigns(:artefact).should be(mock_artefact)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created artefact as @artefact" do
        Artefact.stub(:new).with({'these' => 'params'}) { mock_artefact(:save => true) }
        post :create, :artefact => {'these' => 'params'}
        assigns(:artefact).should be(mock_artefact)
      end

      it "redirects to the created artefact" do
        Artefact.stub(:new) { mock_artefact(:save => true) }
        post :create, :artefact => {}
        response.should redirect_to(artefact_url(mock_artefact))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved artefact as @artefact" do
        Artefact.stub(:new).with({'these' => 'params'}) { mock_artefact(:save => false) }
        post :create, :artefact => {'these' => 'params'}
        assigns(:artefact).should be(mock_artefact)
      end

      it "re-renders the 'new' template" do
        Artefact.stub(:new) { mock_artefact(:save => false) }
        post :create, :artefact => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested artefact" do
        Artefact.stub(:find).with("37") { mock_artefact }
        mock_artefact.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :artefact => {'these' => 'params'}
      end

      it "assigns the requested artefact as @artefact" do
        Artefact.stub(:find) { mock_artefact(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:artefact).should be(mock_artefact)
      end

      it "redirects to the artefact" do
        Artefact.stub(:find) { mock_artefact(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(artefact_url(mock_artefact))
      end
    end

    describe "with invalid params" do
      it "assigns the artefact as @artefact" do
        Artefact.stub(:find) { mock_artefact(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:artefact).should be(mock_artefact)
      end

      it "re-renders the 'edit' template" do
        Artefact.stub(:find) { mock_artefact(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested artefact" do
      Artefact.stub(:find).with("37") { mock_artefact }
      mock_artefact.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the artefacts list" do
      Artefact.stub(:find) { mock_artefact }
      delete :destroy, :id => "1"
      response.should redirect_to(artefacts_url)
    end
  end

end
